import React, { useState, useRef } from 'react';

const WebcamCapture = () => {
  const [image, setImage] = useState('');
  const videoRef = useRef();

  const captureImage = async () => {
    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);
    const imageDataUrl = canvas.toDataURL('image/jpeg');
    const encodedImage = encodeURIComponent(imageDataUrl);
    setImage(encodedImage);

    try {
      const response = await fetch('/saveImage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ image: imageDataUrl }),
      });
      if (response.ok) {
        console.log('Image saved successfully on the server.');
      } else {
        console.error('Failed to save image on the server.');
      }
    } catch (error) {
      console.error('Error occurred while saving image:', error);
    }
  };

  const handleStartCapture = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then(stream => {
        videoRef.current.srcObject = stream;
      })
      .catch(error => {
        console.error('Error accessing webcam:', error);
      });
  };

  const handleStopCapture = () => {
    const stream = videoRef.current.srcObject;
    if (stream) {
      const tracks = stream.getTracks();
      tracks.forEach(track => track.stop());
    }
    videoRef.current.srcObject = null;
  };

  return (
    <div>
      <div>
        <video ref={videoRef} autoPlay muted style={{ maxWidth: '100%', maxHeight: '300px' }}></video>
      </div>
      <div>
        <button onClick={handleStartCapture}>Start Capture</button>
        <button onClick={handleStopCapture}>Stop Capture</button>
        <button onClick={captureImage}>Capture Image</button>
      </div>
      {image && (
        <img
          src={decodeURIComponent(image)}
          alt="Captured"
          style={{ maxWidth: '100%', maxHeight: '300px' }}
        />
      )}
    </div>
  );
};

export default WebcamCapture;
